mapper.select:
SELECT A.BRANCH_NO AS branchNo , A.CREATE_TIME AS createTime , A.CREATE_USER AS createUser , A.MODIFY_TIME AS modifyTime , A.MODIFY_USER AS modifyUser , A.IS_USE AS isUse , A.CUST_CODE AS custCode , A.BANK_CODE AS bankCode , A.BRANCH_STATE_CODE AS branchStateCode , A.LOCAL_CODE AS localCode , A.SITE_NAME AS siteName , A.CLEAN_USER_NO AS cleanUserNo , A.BRANCH_ADDRESS AS branchAddress , A.SORT_ORDR AS sortOrdr , A.CHARGE_MONEY AS chargeMoney , A.PAY_MONEY AS payMoney , A.REMARK AS remark , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 600 AND CODE_ID = A.CUST_CODE ) AS custCodeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 400 AND CODE_ID = A.BANK_CODE ) AS bankCodeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 500 AND CODE_ID = A.BRANCH_STATE_CODE ) AS branchStateCodeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 300 AND CODE_ID = A.LOCAL_CODE ) AS localCodeName , (SELECT USER_NAME FROM T_USER WHERE USER_NO = A.CLEAN_USER_NO ) AS cleanUserName FROM T_BANK_BRANCH A WHERE A.IS_DELETE = 'N'
AND A.CUST_CODE = #{custCode}
AND A.BANK_CODE = #{bankCode}
AND A.BRANCH_STATE_CODE = #{branchStateCode}
AND A.LOCAL_CODE = #{localCode}
AND A.BRANCH_ADDRESS = #{searchWord}
AND A.CLEAN_USER_NO IN (SELECT USER_NO FROM T_USER WHERE USER_NAME = #{searchWord})
AND A.BRANCH_ADDRESS LIKE CONCAT('%', #{searchWord}, '%')
AND A.CLEAN_USER_NO IN (SELECT USER_NO FROM T_USER WHERE USER_NAME = LIKE CONCAT('%', #{searchWord}, '%'))
ORDER BY A.BRANCH_NO ASC
ORDER BY A.BRANCH_NO DESC
LIMIT #{startIdx}, #{recordPerPage}


    SELECT
         COUNT(*) AS CNT
    FROM T_BANK_BRANCH A
   WHERE A.IS_DELETE = 'N'

    
        AND A.CUST_CODE = #{custCode}
    
    
        AND A.BANK_CODE = #{bankCode}
    
    
        AND A.BRANCH_STATE_CODE = #{branchStateCode}
    
    
        AND A.LOCAL_CODE = #{localCode}
    
    
    
        
        	AND A.BRANCH_ADDRESS = #{searchWord}
        
        
        	AND A.CLEAN_USER_NO IN (SELECT USER_NO FROM T_USER WHERE USER_NAME = #{searchWord})
        
    
    
        
        	AND A.BRANCH_ADDRESS LIKE CONCAT('%', #{searchWord}, '%')
        
        
        	AND A.CLEAN_USER_NO IN (SELECT USER_NO FROM T_USER WHERE USER_NAME = LIKE CONCAT('%', #{searchWord}, '%'))

mapper.insert:
        INSERT INTO T_BANK_BRANCH (
              CREATE_TIME
            , CREATE_USER
            , MODIFY_TIME
            , MODIFY_USER
            , IS_DELETE
            , IS_USE
            , CUST_CODE
            , BANK_CODE
            , BRANCH_STATE_CODE
            , LOCAL_CODE
            , SITE_NAME
            , CLEAN_USER_NO
            , BRANCH_ADDRESS
            , SORT_ORDR
            , CHARGE_MONEY
            , PAY_MONEY
            , REMARK
        ) VALUES (
             now()
            ,#{createUser}
            ,now()
            ,0
            ,'N'
            ,'Y'
            ,#{custCode}
            ,#{bankCode}
            ,#{branchStateCode}
            ,#{localCode}
            ,#{siteName}
            ,#{cleanUserNo}
            ,#{branchAddress}
            ,#{sortOrdr}
            ,#{chargeMoney}
            ,#{payMoney}
            ,#{remark}
        )
		
			SELECT LAST_INSERT_ID()

mapper.update:
UPDATE T_BANK_BRANCH SET
      MODIFY_TIME = now()
    , MODIFY_USER = #{modifyUser}
    , CUST_CODE = #{custCode}
    , BANK_CODE = #{bankCode}
    , BRANCH_STATE_CODE = #{branchStateCode}
    , LOCAL_CODE = #{localCode}
    , SITE_NAME = #{siteName}
    , CLEAN_USER_NO = #{cleanUserNo}
    , BRANCH_ADDRESS = #{branchAddress}
    , SORT_ORDR = #{sortOrdr}
    , CHARGE_MONEY = #{chargeMoney}
    , PAY_MONEY = #{payMoney}
    , REMARK = #{remark}
WHERE BRANCH_NO = #{branchNo}

UPDATE T_BANK_BRANCH SET
      IS_DELETE = #{isDelete}
    , MODIFY_USER = #{modifyUser}
    , MODIFY_TIME = now()
WHERE BRANCH_NO = #{branchNo}
