mapper.select:
SELECT A.USER_NO AS userNo , A.CREATE_TIME AS createTime , A.CREATE_USER AS createUser , A.MODIFY_TIME AS modifyTime , A.MODIFY_USER AS modifyUser , A.IS_DELETE AS isDelete , A.USER_ID AS userId , A.USER_NAME AS userName , A.USER_TYPE AS userType , A.CUST_NAME AS custName , A.LOCAL_CODE AS localCode , A.EMAIL AS email , A.PHONE_NUMBER AS phoneNumber , A.USER_STAT AS userStat , A.PASS_WORD AS passWord , A.DEPOSIT_BANK AS depositBank , A.ACCOUNT_NUM AS accountNum , A.REMARK AS remark , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 100 AND CODE_ID = A.USER_TYPE ) AS userTypeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 300 AND CODE_ID = A.LOCAL_CODE ) AS localCodeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 200 AND CODE_ID = A.USER_STAT ) AS userStatName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 400 AND CODE_ID = A.USER_STAT ) AS depositBankName FROM T_USER A WHERE A.IS_DELETE = 'N'
AND A.USER_ID = #{userId}
AND A.USER_STAT = #{userStat}
AND A.IS_DELETE = #{isDelete}
AND A.USER_NO = #{userNo}

SELECT A.USER_NO AS userNo , A.CREATE_TIME AS createTime , A.CREATE_USER AS createUser , A.MODIFY_TIME AS modifyTime , A.MODIFY_USER AS modifyUser , A.IS_DELETE AS isDelete , A.USER_ID AS userId , A.USER_NAME AS userName , A.USER_TYPE AS userType , A.CUST_NAME AS custName , A.LOCAL_CODE AS localCode , A.EMAIL AS email , A.PHONE_NUMBER AS phoneNumber , A.USER_STAT AS userStat , A.PASS_WORD AS passWord , A.DEPOSIT_BANK AS depositBank , A.ACCOUNT_NUM AS accountNum , A.REMARK AS remark , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 100 AND CODE_ID = A.USER_TYPE ) AS userTypeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 300 AND CODE_ID = A.LOCAL_CODE ) AS localCodeName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 200 AND CODE_ID = A.USER_STAT ) AS userStatName , (SELECT CODE_NAME FROM T_CODE WHERE UPER_CODE = 400 AND CODE_ID = A.USER_STAT ) AS depositBankName FROM T_USER A WHERE A.IS_DELETE = 'N'
AND A.USER_TYPE = #{userType}
AND A.USER_STAT = #{userStat}
AND A.LOCAL_CODE = #{localCode}
AND A.USER_NAME = #{searchWord}
AND A.USER_ID = #{searchWord}
AND A.EMAIL = #{searchWord}
AND A.USER_NAME LIKE CONCAT('%', #{searchWord}, '%')
AND A.USER_ID LIKE CONCAT('%', #{searchWord}, '%')
AND A.EMAIL LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY A.USER_NO ASC
ORDER BY A.USER_NO DESC
LIMIT #{startIdx}, #{recordPerPage}


    SELECT
         COUNT(*) AS CNT
    FROM T_USER A
    WHERE A.IS_DELETE = 'N'

    
        AND A.USER_TYPE = #{userType}
    
    
        AND A.USER_STAT = #{userStat}
    
    
        AND A.LOCAL_CODE = #{localCode}
    
    
        
        	AND A.USER_NAME = #{searchWord}
        
        
        	AND A.USER_ID = #{searchWord}
        
        
        	AND A.EMAIL = #{searchWord}
        
    
    
        
        	AND A.USER_NAME LIKE CONCAT('%', #{searchWord}, '%')
        
        
        	AND A.USER_ID LIKE CONCAT('%', #{searchWord}, '%')
        
        
        	AND A.EMAIL LIKE CONCAT('%', #{searchWord}, '%')

mapper.insert:
        INSERT INTO T_USER (
              USER_ID
