            , LOCAL_CODE
            , EMAIL
            , PHONE_NUMBER
            , USER_STAT
            , PASS_WORD
            , DEPOSIT_BANK
            , ACCOUNT_NUM
            , REMARK
            , CREATE_TIME
            , CREATE_USER
            , MODIFY_TIME
            , MODIFY_USER
            , IS_DELETE
        ) VALUES (
              #{userId}
            , #{userName}
            , #{userType}
            , #{custName}
            , #{localCode}
            , #{email}
            , #{phoneNumber}
            , #{userStat}
            , #{passWord}
            , #{depositBank}
            , #{accountNum}
            , #{remark}
            , now()
            , #{createUser}
            , now()
            , #{createUser}
            , 'N'
        )
		
			SELECT LAST_INSERT_ID()

mapper.update:
        UPDATE T_USER SET
              USER_NAME = #{userName}
            , USER_TYPE = #{userType}
            , CUST_NAME = #{custName}
            , LOCAL_CODE = #{localCode}
            , EMAIL = #{email}
            , PHONE_NUMBER = #{phoneNumber}
            , USER_STAT = #{userStat}
            , DEPOSIT_BANK = #{depositBank}
            , ACCOUNT_NUM = #{accountNum}
            , REMARK = #{remark}
            , MODIFY_USER = #{modifyUser}
            , MODIFY_TIME = now()
	        
            , PASS_WORD = #{passWord}
            
        WHERE USER_NO = #{userNo}
